import React, { useEffect, useState } from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();
const [data,setData]=useState([])

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("./signup");
    }
     fetch("http://localhost:5000/allposts", {
       headers: {
         Authorization: "Bearer " + localStorage.getItem("jwt"),
       },
     })
       .then((res) => res.json())
       .then((result) => {
         console.log(result);
         setData(result);
       })
       .catch((err) => console.log(err));
  }, []);

  const likePost =(id)=>{
    fetch("http://localhost:5000/like",{
      method:"put",
      headers:{
        "Content-Type":"application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt")
      },
      body:JSON.stringify({
        postId: id
      })
    }).then(res=>res.json()).then((result)=>{
      const newData = data.map((posts) => {
        if (posts.id == result.id) {
          return result;
        } else {
          return posts;
        }
      });
      setData(newData);
      console.log(result)
    })

  }
   const unlikePost = (id) => {
     fetch("http://localhost:5000/unlike", {
       method: "put",
       headers: {
         "Content-Type": "application/json",
         Authorization: "Bearer " + localStorage.getItem("jwt"),
       },
       body: JSON.stringify({
         postId: id,
       }),
     })
       .then((res) => res.json)
       .then((result) => {
        const newData = data.map((posts)=>{
          if(posts.id == result.id){
            return result
          }else{
            return posts
          }
        })
        setData(newData)
         console.log(result);
       });
   };

  return (
    <div className="home">
      {data.map((posts)=>{
        return (
          <div className="card">
            <div className="card-header">
              <div className="card-pic">
                <img
                  src="https://images.unsplash.com/photo-1597589827317-4c6d6e0a90bd?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8M3x8c3F1YXJlfGVufDB8fDB8fHww&auto=format&fit=crop&w=1000&q=60"
                  alt=""
                />
              </div>
              <h5>{posts.postedBy.name}</h5>
            </div>
            <div className="card-image">
              <img src={posts.photo} alt="" />
            </div>
            <div className="card-content">
              {posts.likes.includes(
                JSON.parse(localStorage.getItem("user"))._id
              ) ? (
                <span
                  class="material-symbols-outlined material-symbols-outlined-red"
                  onClick={() => {
                    unlikePost(posts._id);
                  }}
                >
                  favorite
                </span>
              ) : (
                <span
                  class="material-symbols-outlined"
                  onClick={() => {
                    likePost(posts._id);
                  }}
                >
                  favorite
                </span>
              )}

              <p>{posts.likes.length} Likes</p>
              <p>{posts.body}</p>
            </div>
            <div className="add-comment">
              <span class="material-symbols-outlined">mood</span>
              <input type="text" placeholder="Add a comment" />
              <button className="comment">Post</button>
            </div>
          </div>
        );
      })}
      
    </div>
  );
}
